    public function testGetComplement27() {
		$this->object->setHSL( 27.0, 0.3, 0.6 );
		$complement = $this->object->getComplement();
		$ret = $complement->getHSL();
		$this->assertEquals( 27.0 + 180.0, $ret['h'], "Wert: 27.0, Erwartet: " . 27.0 + 180.0 . ", Erhalten: " . $ret['h'] );
	}

    public function testGetComplement180() {
		$this->object->setHSL( 180.0, 0.3, 0.6 );
		$complement = $this->object->getComplement();
		$ret = $complement->getHSL();
		$this->assertEquals( 0.0, $ret['h'], "Wert: 180.0, Erwartet: 0.0, Erhalten: " . $ret['h'] );
	}

    public function testShortRGBStringToRGBRValue() {
		$this->object->setRGBString( '123' );
		$ret = $this->object->getRGB();
		$this->assertEquals( 1 * 16 + 1, $ret['r'], "Erwartet: " . 1 * 16 + 1 . ", Erhalten: " . $ret['r'] );
    }

