	/**
	 * @param string $hex_1 HTML color #xxxxxx
	 * @param string $hex_2 HTML color #xxxxxx
	 * @param int $delta
	 * @return true if similar
	 */
	function isSimilar( $hex_1, $hex_2, $delta = 1 ) {
		if ( ( preg_match( '/^#?([a-fA-F0-9]{2})([a-fA-F0-9]{2})([a-fA-F0-9]{2})$/', $hex_1, $val_1 ) )
		  && ( preg_match( '/^#?([a-fA-F0-9]{2})([a-fA-F0-9]{2})([a-fA-F0-9]{2})$/', $hex_2, $val_2 ) ) ) {
			$ret = true;
			for ( $i = 1; $i < 4; $i++ ) {
				if ( abs( hexdec( $val_1[$i] ) - hexdec( $val_2[$i] ) ) > abs( $delta ) ) {
					$ret = false;
				}
			}
		} else {
			$ret = false;
		}
		return $ret;
	}


